<?php namespace ChapmanRadio;
require_once BASE."/lib/facebook.php";$facebook = new \Facebook(array(	'appId'  => Site::$FacebookAppId,	'secret' => Site::$FacebookAppSecret,	'cookie' => true,	));// See if there is a user from a cookie$me = $facebook->getUser();Template::script("\$(document).ready(function(){	$(document).on('click', '.loginButton__facebook', function(){ FB.login(); });	window.fbAsyncInit = function() {		FB.init({			appId: '".Site::$FacebookAppId."',			cookie: true,			xfbml: true,			oauth: true			});		FB.Event.subscribe('auth.login', function(response) {			window.location.reload();			});		FB.Event.subscribe('auth.logout', function(response) {			window.location.reload();			});		};	(function() {		var e = document.createElement('script'); e.async = true;		e.src = document.location.protocol +		'//connect.facebook.net/en_US/all.js';		document.getElementById('fb-root').appendChild(e);		}());	});");if ($me) {	try {		// Proceed knowing you have a logged in user who's authenticated.		$me = $facebook->api('/me');		}	catch (\FacebookApiException $e) {		// Log::Error('FB EX', $e);		$me = NULL;		}	}$fbid = isset($me['id']) ? $me['id'] : 0;