<?php namespace ChapmanRadio;class Report {		public static $colors = array(		"tardy" => "#A60",		"absent" => "#D00",		"present" => "#090",		"excused" => "#66F",		"optional" => "#A0A",		"cancelled" => "#F90",		"absent_again" => "#900"		);			public static function dispLate($row) {		if($row['status'] != 'present') return "";		$late = $row['late'];		$s = ($late == 1 || $late == -1) ? "" : "s";		if($late < 0) return "<span style='color:#090'>".(0-$late)." minute$s early</span>";		else if($late == 0) return "<span style='color:#848484'>on time</span>";		else if($late <= Site::$TardyGrace) return "<span style='color:#A60;'>$late minute$s late</span>";		else return "<span style='color:#A00;'>$late minute$s late</span>";		}			}