<?php namespace ChapmanRadio;class Validation {		private $errors = array();		public function HasErrors(){		return !empty($this->errors);		}			public function FirstError(){		return empty($this->errors) ? null : $this->errors[0];		}		public function Required($field, $msg){		if(Request::Get($field, '') === '') $this->errors[] = $msg;		}			public function Email($field, $msg){		if(!preg_match("/^[\w-\._\+%]+@(?:[\w-]+\.)+[\w]{2,6}$/", Request::Get($field, ''))) $this->errors[] = $msg;		}			public function EmailNotUsed($field, $msg){		$temp = DB::GetFirst("SELECT userid FROM users WHERE email = :email", array(":email" => Request::Get('email')));		if($temp) $this->errors[] = $msg;		}		public function EmailChapman($field, $msg){		if(!preg_match("/^[A-Za-z0-9._%+-]+@(mail\.|)chapman\.edu$/", Request::Get($field, ''))) $this->errors[] = $msg;		}			public function Matches($field1, $field2, $msg){		if(Request::Get($field1, '') !== Request::Get($field2, '')) $this->errors[] = $msg;		}			public function Integer($field, $msg){		self::Required($field, $msg);		if(!intval(Request::Get($field, ''))) $this->errors[] = $msg;		}		}	