<?php$included = true;require('backend.php');$base = $_SERVER['DOCUMENT_ROOT'].'/content/';$is = list_files($base, true);echo "Start Thumbnail Creation:<br />\r\n";ob_end_flush();foreach($is as $i){	$file = ConvertImage($base.$i);	if(strpos($file, 'ERROR:')===0){		echo " * $file<br />\r\n";		flush();		}	else if($file != $base.$i){		echo " * Converted Image: To JPG<br />\r\n";		flush();		}			else if(!file_exists(thumbname($file)) && !is_thumb($file)){		$r = Thumbnail($file);		if($r === 'Success') echo " * Created thumbnail of $file<br />\r\n";		else echo " * ERROR Creating thumbnail of $file ($r)<br />\r\n";		flush();		}	else if(is_thumb($file) && !file_exists(imgname($file))){		if(unlink($file)) echo " * Deleted thumbnail of $file<br />\r\n";		else echo " * ERROR Deleting thumbnail of $file<br />\r\n";		flush();		}	}echo 'Done';?>